import React, { Component } from 'react';
import Search from './Search'
import Setcontent from './Setcontent'

class App extends Component {

  state = {
    query: null,
    bookInfo: null,
    authors: null,
    content: null,
    price: null,
    publisher: null,
    status: null,
    title: null
  };

  componentDidMount() {}

  _getQueryValue = async value => {
    await this.setState({ query: value });
    this._getBooks();
  };

  _getBooks = async () => {
    const bookInfo = await this._callApi();


    console.log(this.state);
  };

  _callApi = () => {

    let headers = { Authorization: 'KakaoAK '+ '227c557d727e7912353d9bcb15d9f333'}

    const _init = {
      method: 'get',
      headers: headers,
    };
    // let a;
    fetch(`https://dapi.kakao.com/v3/search/book?query=${this.state.query}`, _init)
    .then(response => response.json())
    .then(json => this.setState({
      // bookInfo:json.documents[0].authors[0]
      authors: json.documents[0].authors[0],
      content: json.documents[0].content,
      price: json.documents[0].price,
      publisher: json.documents[0].publisher,
      status: json.documents[0].status,
      title: json.documents[0].title,
    })
    );

    return fetch(`https://dapi.kakao.com/v3/search/book?query=${this.state.query}`, { headers })
    .then(res => res.json)
    .then(json => json.documents)
    .catch(err => console.log(err))
  };
  render() {
    return (
      <div className="App">
        <Search books={this._callApi} query={this._getQueryValue}/>
        <Setcontent 
        // title={this.state.title} 
        authors={this.state.authors} 
        contents={this.state.contents} 
        price={this.state.price}
        publisher={this.state.publisher}
        status={this.state.status}
        thumnail={this.state.thumnail} />
      </div>

    )
  }
}

export default App;
