    .then(json => this.setState({
      bookInfo:json.documents[0].authors[0]
    })
    );


    return fetch(`https://dapi.kakao.com/v3/search/book?query=${this.state.query}`, { headers })
    .then(res => res.json)
    .then(json => json.documents)
    .catch(err => console.log(err))

  };
  render() {
    return (
      <div className="App">
        <Search books={this._callApi} query={this._getQueryValue}/>
        <Setcontent title={this.state.query} authors={this.state.bookInfo} contents="contents" price="price" thumnail="thumnail" />
      </div>

    )
  }
}

export default App;
